@import "./fontello-4b338c27/css/fontello.css";

body {
  margin: 0;
  background-color: black;
  background-image: url("static/pics/backg.jpg");
  background-position: center;
  background-repeat: repeat;
  // background-size: 20px;
  color: white;
  font-family: "PT Serif", serif;
  overflow-x: hidden;
}

.slide {
  height: 100vh;
}

.top {
  // color: black;
  // background-image: url("static/pics/backg.png");
  position: relative;

  .headline {
    padding-top: 10rem;
    font-size: 7rem;
    margin-bottom: 1rem;
    margin-top: 0;
    text-align: center;
  }

  .subhed {
    font-size: 1.5rem;
    text-align: center;
    margin: 0 auto;
    // max-width: 500px;
    font-family: "Open Sans", sans-serif;
  }
  .byline {
    font-size: 1.4rem;
    font-family: "PT Serif", serif;
    text-align: center;
    font-weight: bold;
    margin: 2rem 0;
  }
}

.go-down {
  position: absolute;
  width: 50px;
  height: 50px;
  bottom: 10px;
  border-radius: 50%;
  border: 2px solid white;
  left: 50%;
  -webkit-transform: translateX(-50%);
  transform: translateX(-50%);
  opacity: 0.9;
  cursor: pointer;
  i {
    left: 14px;
    top: 11px;
    position: relative;
  }
}

.bounce {
  -moz-animation: bounce 4s infinite;
  -webkit-animation: bounce 4s infinite;
  animation: bounce 4s infinite;
}

@keyframes bounce {
  0%,
  20%,
  50%,
  80%,
  100% {
    transform: translateY(0);
  }
  40% {
    transform: translateY(-10px);
  }
  60% {
    transform: translateY(-5px);
  }
}

.photos {
  position: relative;
  width: 100vw;
  z-index: 10;
  vertical-align: top;
  text-align: center;
  .photo-container {
    display: inline-block;
    width: 150px;
    height: 150px;
    cursor: pointer;

    img {
      clip-path: circle(50px at center);
      width: 70px;
      margin: 15px;
      pointer-events: none;
      &:hover {
        width: 150px;
        margin: -25px;
        clip-path: circle(80px at center);
        z-index: 100;
      }
    }
  }

  .photo-row {
    margin-top: 70px;
    text-align: center;
    width: 70%;
    display: inline-block;
    vertical-align: top;
  }

  .family-name {
    font-family: "Open Sans", sans-serif;
    font-weight: bold;
    margin-bottom: 30px;
  }
}

.text-slide {
  position: relative;
  font-family: "Open Sans", sans-serif;
  overflow-y: hidden;
  p.slide-words {
    max-width: 500px;
    margin: 0 auto;
    position: relative;
    top: 40vh;
    font-size: 1.2rem;
    background-color: #3a3a3a50;
    padding: 20px;
    border-radius: 10px;
    z-index: 3;
    pointer-events: none;
  }

  // .slide-pics {
  //   width: 1;
  //   position: absolute;
  // }
  // text-align: right;
  // margin-right: 50px;

  &:not(.text-map):not(.text-news) .slide-pics:hover {
    img {
      transform: rotate(5deg);
    }
    .hover-highlight {
      opacity: 1;
    }
    .no-highlight {
      opacity: 0;
    }
  }
  &:not(.text-map) {
    .slide-pics {
      position: absolute;
      top: 10px;
      z-index: 2;
      width: 600px;
      height: 100%;
    }
    img {
      position: absolute;
    }
  }

  .hover-highlight {
    opacity: 0;
    z-index: 2;
  }
  // }
}

.pic-description {
  height: 80px;
  width: 200px;
  text-align: center;
  padding: 5px;
  position: absolute;
  opacity: 0;
  z-index: 100;
  background: rgb(244, 242, 242);
  color: black;
  border-radius: 10px;
  font-weight: bold;
  pointer-events: none;

  p {
    margin: 10px;
    font-size: 1rem;
  }

  &.show {
    animation-fill-mode: forwards;
    animation: show 1s infinite;
  }

  @keyframes show {
    0% {
      opacity: 0;
    }
    100% {
      opacity: 1;
    }
  }
}

.text-map {
  position: relative;

  .wandhama-locator {
    // z-index: -1;
    // position: relative;
    z-index: -1;
    position: absolute;
    // height: 600px;
    vertical-align: top;
    display: inline-block;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;

    .overlay-dot {
      animation: pulse 2s infinite;
      @keyframes pulse {
        0% {
          opacity: 0;
        }
        50% {
          opacity: 1;
        }
        100% {
          opacity: 0;
        }
      }
    }
    .wandhama-village {
      opacity: 0;
      height: 50%;
      left: 0;
    }

    &:hover {
      .wandhama-village {
        opacity: 1;
      }
    }

    // height: 600px;
    // text-align: right;
    img {
      // right: 20px;
      height: 100%;
      position: absolute;
      right: 0;
      // height: 100%;
    }
  }
}

.text-bodies {
  p {
    // text-align: right;
    // margin-right: 100px;
    background-color: #3a3a3a80;
    z-index: 3;
  }
  // .wandhama-bodies {
  //   // text-align: right;
  //   // margin-right: 50px;
  //   &:hover {
  //     img {
  //       transform: rotate(5deg);
  //     }
  //     .wandhama-bodies-highlight {
  //       opacity: 1;
  //     }
  //     .wandhama-bodies {
  //       opacity: 0;
  //     }
  //   }
  //   img {
  //     right: 200px;
  //   }

  //   .wandhama-bodies-highlight {
  //     opacity: 0;
  //     z-index: 2;
  //   }
  .slide-pics {
    right: 10px;
    top: 10px !important;
  }
  // }
}

.text-failed {
  .slide-pics {
    right: 100px;
    top: 50px !important;
  }
  // p {
  //   right: 100px;
  // }
  // p {
  //   background-color: #3a3a3aaa;
  // }
}

.text-victims {
  // height: 150vh;
  .slide-pics {
    right: 80px;
  }

  img {
    height: 600px;
    // right: 100px;
    opacity: 0.8;
  }
}

.text-house {
  .wandhama-house {
    // text-align: right;
    margin-left: 50px;
  }
}

p.darker {
  background-color: #3a3a3ac1 !important;
}

.text-burning {
  .slide-pics {
    height: 600px;
    left: 100px;
    opacity: 0.8;
    &.child-pic {
      right: 100px;
      left: auto;
      top: 0px;
      img {
        height: 600px;
        left: auto;
        right: 0px;
      }
    }
  }
  img {
    height: 100%;
  }
}

.splide__pagination__page {
  background: #6a0000;
  border: 1px solid rgb(255, 135, 135);
}

.splide__pagination__page.is-active {
  background: rgb(255, 121, 121);
}

.splide__arrow {
  position: absolute;
  z-index: 1;
  bottom: 0px;
  width: 50px;
  height: 50px;
  border-radius: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  border: none;
  padding: 0;
  opacity: 1;
  background: none;
  color: white;
  font-size: 20px;
}

.language-toggle {
  z-index: 10000;
  p {
    text-align: center;
    font-weight: bold;
    color: white;
    font-size: 1.2rem;
  }
  span {
    cursor: pointer;
  }
  width: 150px;
  margin: 50px auto;
  font-family: "Open Sans", sans-serif;
}
.text-tweets {
  background-image: url("static/pics/tweets.png");
  background-size: cover;
  .tweets {
    text-align: center;
    position: relative;
    top: 40px;
    color: black;

    .tweet {
      padding: 10px;
      display: inline-block;
      vertical-align: top;
    }
    .slide-heading {
      font-weight: bold;
      font-size: 1.8rem;
      text-shadow: 1px 1px 3px white, 1px -1px 3px white, -1px 1px 3px white;
    }
    .hashtags {
      font-weight: bold;
      text-shadow: 1px 1px 3px white, 1px -1px 3px white, -1px 1px 3px white;
    }
  }
}

.news {
  background-image: url("static/pics/news.png");
  background-size: cover;
  background-position: center;
  height: 100vh;

  position: relative;
  div {
    position: absolute;
    width: 100%;
    height: 100%;
    text-align: center;

    p {
      position: absolute;
      font-size: 10rem;
      font-weight: bold;
      // width: 100%;
      // height: 100%;
      margin: 0px;
      top: 50%;
      left: 50%;
      -webkit-transform: translate(-50%, -50%);
      transform: translate(-50%, -50%);
      color: white;
      text-shadow: 1px 1px 10px black;
      font-family: "PT Serif", serif;
    }
  }
  // img {
  //   display: inline-block;
  //   max-width: 300px;
  // }
}

.hide-on-desktop {
  display: block;
}

.hide-on-mobile {
  display: none;
}

@media (max-width: 500px) {
  .top {
    .headline {
      font-size: 3rem;
    }
    .subhed {
      max-width: 250px;
      font-size: 1.2rem;
    }
  }
  .slide-pics {
    width: 100% !important;
    position: relative;
  }
  .slide-pics {
    right: 0;
    left: 0;

    img {
      width: 95%;
      top: 10%;
      left: 2.5%;
      max-height: 100%;
      // right: 0px;
      // left: 0px;
    }
  }
  .text-house .wandhama-house {
    margin-left: 0px;
  }
  .text-burning .slide-pics {
    left: 0px;
    opacity: 0.8;
    &.child-pic {
      right: 0px;
      img {
        max-width: 90%;
        height: auto;
      }
      top: 0px;
    }
  }
  .text-victims {
    img {
      height: auto;
      width: 95%;
      top: 10%;
    }
  }
  .news {
    background-image: url("static/pics/news-mobile.png");
    div {
      p {
        font-size: 6rem;
      }
    }
  }

  .hide-on-desktop {
    display: none;
  }

  .hide-on-mobile {
    display: block;
  }

  .text-tweets {
    .tweets {
      top: 40px;
      .tweet {
        width: 80%;
        height: 100%;
        padding: 0px;
      }
    }
  }

  .photos {
    .photo-row {
      width: 100%;
      .photo-container {
        display: inline-block;
        width: 80px;
        height: 80px;

        img {
          clip-path: circle(50px at center);
          width: 60px;
          margin: 15px;
          pointer-events: none;
          &:hover {
            width: 150px;
            margin: -25px;
            clip-path: circle(80px at center);
            z-index: 100;
          }
        }
      }
    }
  }
}

@media (max-width: 769px) {
  .photos {
    .photo-row {
      width: 100%;
      margin-top: 120px;
    }
  }
}
