@import "./fontello-4b338c27/css/fontello.css";

body {
  margin: 0;
  background-color: black;
  background-image: url("static/pics/backg.jpg");
  background-repeat: repeat;
  // background-size: 20px;
  color: white;
  font-family: "PT Serif", serif;
  overflow-x: hidden;
}

.slide {
  height: 100vh;
}

.top {
  // color: black;
  // background-image: url("static/pics/backg.png");
  position: relative;

  .headline {
    padding-top: 10rem;
    font-size: 7rem;
    margin-bottom: 1rem;
    margin-top: 0;
    text-align: center;
  }

  .subhed {
    font-size: 1.5rem;
    text-align: center;
    margin: 0 auto;
    // max-width: 500px;
    font-family: "Open Sans", sans-serif;
  }
  .byline {
    font-size: 1.4rem;
    font-family: "PT Serif", serif;
    text-align: center;
    font-weight: bold;
    margin: 2rem 0;
  }
}

.go-down {
  position: absolute;
  width: 50px;
  height: 50px;
  bottom: 10px;
  border-radius: 50%;
  border: 2px solid white;
  left: 50%;
  -webkit-transform: translateX(-50%);
  transform: translateX(-50%);
  opacity: 0.9;
  cursor: pointer;
  i {
    left: 14px;
    top: 11px;
    position: relative;
  }
}

.bounce {
  -moz-animation: bounce 4s infinite;
  -webkit-animation: bounce 4s infinite;
  animation: bounce 4s infinite;
}

@keyframes bounce {
  0%,
  20%,
  50%,
  80%,
  100% {
    transform: translateY(0);
  }
  40% {
    transform: translateY(-10px);
  }
  60% {
    transform: translateY(-5px);
  }
}

.photos {
  position: relative;
  width: 100vw;
  z-index: 10;
  vertical-align: top;
  text-align: center;
  .photo-container {
    display: inline-block;
    width: 100px;

    img {
      clip-path: circle(50px at center);
      width: 70px;
      margin: 15px;
      cursor: pointer;
      &:hover {
        width: 150px;
        margin: -25px;
        clip-path: circle(80px at center);
        z-index: 100;
      }
    }
  }

  .photo-row {
    margin-top: 150px;
    text-align: center;
    width: 20%;
    display: inline-block;
    vertical-align: top;
  }

  .family-name {
    font-family: "Open Sans", sans-serif;
    font-weight: bold;
    margin-bottom: 30px;
  }
}

.text-slide {
  position: relative;
  font-family: "Open Sans", sans-serif;
  p.slide-words {
    max-width: 500px;
    margin: 0 auto;
    position: relative;
    top: 40vh;
    font-size: 1.2rem;
    background-color: #3a3a3a50;
    padding: 20px;
    border-radius: 10px;
    z-index: 3;
    pointer-events: none;
  }

  // .slide-pics {
  //   width: 1;
  //   position: absolute;
  // }
  // text-align: right;
  // margin-right: 50px;

  &:not(.text-map):not(.text-news) .slide-pics:hover {
    img {
      transform: rotate(5deg);
    }
    .hover-highlight {
      opacity: 1;
    }
    .no-highlight {
      opacity: 0;
    }
  }
  &:not(.text-map) {
    .slide-pics {
      position: absolute;
      top: 10px;
      z-index: 2;
      width: 600px;
      height: 100%;
    }
    img {
      position: absolute;
    }
  }

  .hover-highlight {
    opacity: 0;
    z-index: 2;
  }
  // }
}

.pic-description {
  height: 80px;
  width: 200px;
  text-align: center;
  padding: 5px;
  position: absolute;
  opacity: 0;
  z-index: 100;
  background: rgb(244, 242, 242);
  color: black;
  border-radius: 10px;
  font-weight: bold;
  pointer-events: none;

  p {
    margin: 10px;
    font-size: 1rem;
  }

  &.show {
    animation-fill-mode: forwards;
    animation: show 1s infinite;
  }

  @keyframes show {
    0% {
      opacity: 0;
    }
    100% {
      opacity: 1;
    }
  }
}

.text-map {
  position: relative;

  .wandhama-locator {
    // z-index: -1;
    // position: relative;
    z-index: -1;
    position: absolute;
    // height: 600px;
    vertical-align: top;
    display: inline-block;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;

    .overlay-dot {
      animation: pulse 2s infinite;
      @keyframes pulse {
        0% {
          opacity: 0;
        }
        50% {
          opacity: 1;
        }
        100% {
          opacity: 0;
        }
      }
    }
    .wandhama-village {
      opacity: 0;
      height: 50%;
      left: 0;
    }

    &:hover {
      .wandhama-village {
        opacity: 1;
      }
    }

    // height: 600px;
    // text-align: right;
    img {
      // right: 20px;
      height: 100%;
      position: absolute;
      right: 0;
      // height: 100%;
    }
  }
}

.text-bodies {
  p {
    // text-align: right;
    // margin-right: 100px;
    background-color: #3a3a3a80;
    z-index: 3;
  }
