  }

  img {
    height: 600px;
    // right: 100px;
    opacity: 0.8;
  }
}

.text-house {
  .wandhama-house {
    // text-align: right;
    margin-left: 50px;
  }
}

p.darker {
  background-color: #3a3a3ac1 !important;
}

.text-burning {
  .slide-pics {
    height: 600px;
    left: 100px;
    opacity: 0.8;
    &.child-pic {
      right: 100px;
      left: auto;
      top: 100px;
      img {
        height: 400px;
        left: auto;
        right: 0px;
      }
    }
  }
  img {
    height: 100%;
  }
}

.splide__pagination__page {
  background: #6a0000;
  border: 1px solid rgb(255, 135, 135);
}

.splide__pagination__page.is-active {
  background: rgb(255, 121, 121);
}

.splide__arrow {
  position: absolute;
  z-index: 1;
  bottom: 0px;
  width: 50px;
  height: 50px;
  border-radius: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  border: none;
  padding: 0;
  opacity: 1;
  background: none;
  color: white;
  font-size: 20px;
}

.language-toggle {
  z-index: 10000;
  p {
    text-align: center;
    font-weight: bold;
    color: white;
    font-size: 1.2rem;
  }
  span {
    cursor: pointer;
  }
  width: 150px;
  margin: 50px auto;
  font-family: "Open Sans", sans-serif;
}

.tweets {
  text-align: center;
  position: relative;
  top: 90px;
  .tweet {
    padding: 10px;
    display: inline-block;
    vertical-align: top;
  }
  .slide-heading {
    font-weight: bold;
    font-size: 1.4rem;
  }
  .hashtagsa {
    font-weight: bold;
  }
}

.news {
  background-image: url("static/pics/news.png");
  background-size: cover;
  height: 100vh;
